import React, {useState, useEffect} from 'react';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import HomePage from "./views/HomePage";
import LoginPage from "./views/LoginPage";
import RegisterPage from "./views/RegisterPage";
import axios from "axios"
function App(){
  const [modal, setModal] = useState(false);
  const [user, setUser] = useState([])

  let [isAuthenticated, setisAuthenticated] = useState(localStorage.getItem('token') ? true : false)

  const userHasAuthenticated = (authenticated, username, token) => { 
    setisAuthenticated(authenticated)
    setUser(username)
    localStorage.setItem('token', token);
  }//회원가입이나 로그인이 성공했을 때 토큰을 저장

  const handleLogout = () => {
    setisAuthenticated(false)
    setUser("")
    localStorage.removeItem('token');
    // setModal(false)
  }//로그아웃

  useEffect(()=>{
    if(isAuthenticated){
      setModal(true)
    }
    else{
      setModal(false)
    }
  },[isAuthenticated])

  useEffect(() => {
    if(isAuthenticated) {
      //Token 값이 타당간가 /validate 요청을 통해 확인하고
      // 상태 코드가 200이라면 /api/current 요청으로 user정보 받아옴
      fetch('http://39.118.174.168:8000/api/validate/', {

        headers: {
          Authorization: `JWT ${localStorage.getItem('token')}`
        }
      })
      .then(res => {
        fetch('http://localhost:8000/api/current/', {
          method: 'GET',
          headers: {
            Authorization: `JWT ${localStorage.getItem('token')}`
          }
        })
        .then(res => res.json())
        .then(json => {
          if(json.username) {
            setUser(json.username);
          }
          else{
            //유저가 등록되지 않았다면 로그인 버튼이 나오도록
            setModal(false)
            setisAuthenticated(false)
        }
        // Refresh Token을 발급 -> 만료 시간 연장
        fetch('http://localhost:8000/api/refresh/', {
          method : 'POST',
          headers: {
            'Content-Type' : 'application/json'
          },
          body: JSON.stringify({
            token: localStorage.getItem('token')
          })
        })
        .then(res => res.json())
        .then((json)=>{
          userHasAuthenticated(true, json.user.username, json.token);
        })
        .catch(error => {
          console.log(error);
        });
      })
      .catch(error => {
        handleLogout();
        console.log(error);
      });
    })
    .catch(error => {
      handleLogout();
      console.log(error);
    })
  }
}, [isAuthenticated])


  return (
    <Router>
      <div>
        <Switch>
          <Route exact path="/" component={HomePage} />
          <Route exact path="/login">
            <LoginPage setModal={setModal} userHasAuthenticated={userHasAuthenticated}/>
          </Route>
          <Route exact path="/register" component={RegisterPage} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
