import { REGISTER_USER, LOGIN_USER, LOGOUT_USER } from "./type";
import { request } from "../utils/axios";

const USER_URL = "api";
const headers = {
  'Content-Type':'application/json'
};

export function registerUser(dataToSubmit) {
  const data = request("post", USER_URL + "/create/", headers, dataToSubmit);
  return {
    type: REGISTER_USER,
    payload: data,
  };
}

export function loginUser(dataToSubmit) {
  const data = request("post", USER_URL+ "/login/", headers, dataToSubmit);
  console.log(data);
  return {
    type: LOGIN_USER,
    payload: data,
  };
}

export function logoutUser() {
  const data = request("post", USER_URL + "/logout/", headers);
  return {
    type: LOGOUT_USER,
    payload: data,
  };
}
