import React, { useState, useEffect} from 'react';
import { useHistory } from 'react-router'
import CKEditor from 'ckeditor4-react';
import {Form} from "react-bootstrap";
import '../views/Boardwrite.css';




  const titleStyle = {
     marginBottom: 9
   };

   
function Boardwrite() {


  let [filterList] = useState([{id : 1, language: '챌린져'}, {id : 2, language : '그랜드마스터'},
    {id : 3, language : '마스터'}, {id : 4, language :'다이아몬드'}, {id : 5, language:'플레티넘'},
    {id : 6, language:'골드'}, {id : 7, language:'실버'}, {id : 8, language:'브론즈'}, {id : 9, language:'아이언'}])
    let [languagefilterList, setLanguageFilterList] = useState("")
    
    let Today = new Date();
    let date = Today.getFullYear() + "-" + Today.getMonth() + "-" + Today.getDate()
    

    const history = useHistory()
    let [title , setTitle] = useState("")
    let [content , setContent] = useState("")
    let [userPhoto, setUserPhoto] = useState()
    let [user_id, setUser_id] = useState()
    let [userNickname, setUserNickname] = useState()
    let sendData;

    const handleEffect = (handleSubmit) => {
        if(languagefilterList === "" || languagefilterList.length === 0){
            alert("한 개 이상의 언어를 선택해주세요.")
            return;
        }
        sendData = {
            title : title,
            content : content,
            date : date,
            like : 0,
            nickname : userNickname,
            language : languagefilterList,
            profileImage : userPhoto,
            user_id : user_id
        }
        handleSubmit()
    }

    const handleSubmit = (e) => {
        let form_data = new FormData();

        form_data.append('title', sendData.title);
        form_data.append('content', sendData.content);
        form_data.append('date', sendData.date);
        form_data.append('language', sendData.language);
        form_data.append('like', sendData.like);
        form_data.append('nickname', sendData.userNickname);
        form_data.append('profileImage', sendData.profileImage)
        form_data.append('user_id', sendData.user_id);

        fetch("http://39.118.174.168:8000/board/ckeditor/upload/", {
            method : 'POST',
            headers: {
                Authorization : `JWT ${localStorage.getItem('token')}`,
            },
            body : form_data
        })
        .then(res => res.json())
        .catch(error => console.error('Error:', error))
        .then(response => console.log('Success:', JSON.stringify(response)))
        .then(history.push('/primium'))
        .then(history.go(0));
    };

    useEffect(()=>{
        fetch('http://39.118.174.168:8000/api/current_user/', {
          headers: {
            Authorization: `JWT ${localStorage.getItem('token')}`
          }
        })
        .then(res => res.json())
        .then(json => {
          if (json.id === null || json.id === undefined) {
            alert("로그인이 필요합니다.")
            // history.push('/')
        }fetch('http://39.118.174.168:8000/api/profile/' + json.id + '/update/',{
                method : 'PATCH',
                headers: {
                    Authorization: `JWT ${localStorage.getItem('token')}`
                },
            })
            .then((res)=>res.json())
            .then((userData)=> {
                setUserPhoto(userData.photo)
                setUser_id(userData.user_id)
                setUserNickname(userData.userNickname)
                
            })
            .catch(error => {
                console.log(error);
              });;
        }).catch(error => {
            console.log(error)
          });
    },[userPhoto])

    const ClickFilter = (lang)=>{
      let Num = 0;
      let List = [...languagefilterList]
      
      List.map((a)=>{
          if(a === lang){
              Num = 1;
          }
          return Num;
      })
      if (Num === 0) {
          List.push(lang)
      }else{
          List.splice(List.indexOf(lang),1)
      }
      setLanguageFilterList(List)
  }


  return (
    <>
    <div className="container-section">
    <h2>글쓰기</h2>
    <Form.Control
          type = "text"
          style = {titleStyle}
          placeholder ="제목을 입력하세요"
          onChange = {(e)=>{setTitle(e.target.value)}}
        />
    
    <CKEditor data=""
              onChange = {(e)=>{setContent(e.target.value)}}
             />
             
             <button className="upButton" onClick={()=>{
                            handleEffect(handleSubmit)
                            
                        }}>출간하기</button>
                        </div>
                        {

          <aside className="pDRpRs">
                    <div className="eyrfCGs">
                    <div className="filter__heads" />
              <ul style={{ listStyleType: "none"}}>
                    {
                    filterList.map((a)=>{
                        return(
                        <li key={a.id}>
                            <input id={a.language} className="filters-input__checkboxs" value="action" type="checkbox" data-type="genres"></input>
                            <label className="input__labels" htmlFor={a.language} onClick={()=>{ClickFilter(a.language)}}>
                            <span>
                                {a.language}</span>
                            <span className="filters-input__ticks">
                                <svg 
                                width="13"
                                height="13">
                                <use xlinkHref="#check">
                                    <svg viewBox="0 0 24 24" id="check" xmlns="http://www.w3.org/2000/svg"><path d="M9 21.035l-9-8.638 2.791-2.87 6.156 5.874 12.21-12.436L24 5.782z"></path></svg>
                                </use>
                                </svg>
                            </span>
                            </label>
                        </li>
                        )
                    })
                    }
                </ul>
                
                    </div>  
            </aside>
                    }
    </>
  )
}
export default Boardwrite;
