import { REGISTER_USER, LOGIN_USER, LOGOUT_USER } from "./type";
import { request } from "../utils/axios";
import { config } from "rxjs";


const USER_URL = "api";

export const token = () => (getState) =>{
  const token = getState().auth.token;

  const config = {
    headers :{
      "Content-Type":"application/json"
    }
  };

  if(token){
    config.headers['Authorization'] = `Token ${token}`;
  }
}

export function registerUser(dataToSubmit) {
  const data = request("post", USER_URL + "/create/", dataToSubmit);
  return {
    type: REGISTER_USER,
    payload: data,
  };
}

export function loginUser(dataToSubmit) {
  const All = dataToSubmit + config;
  alert(All);
  const data = request("post", USER_URL+ "/login/", All);
  return {
    type: LOGIN_USER,
    payload: data,
  };
}

export function logoutUser() {
  const data = request("post", USER_URL + "/logout/");
  return {
    type: LOGOUT_USER,
    payload: data,
  };
}
