import React, { Component } from "react";
import CKEditor from "ckeditor4-react";
import Button from '@material-ui/core/Button';


const headers = { withCredentials: true };

class BoardWrite extends Component {
  state = {
    data: ""
  };

  componentDidMount() {
    if (this.props.location.query !== undefined) {

    }
  }

  componentWillMount(){
    if (this.props.location.query !== undefined) {
      this.setState({

      });
    }
  }

  writeBoard = () => {
    let url;
    let send_param;

    const boardTitle = this.boardTitle.value;
    const boardContent = this.state.data;

    if (boardTitle === undefined || boardTitle === "") {
      alert("글 제목을 입력 해주세요.");
      boardTitle.focus();
      return;
    } else if (boardContent === undefined || boardContent === "") {
      alert("글 내용을 입력 해주세요.");
      boardContent.focus();
    }
    
    if (this.props.location.query !== undefined) {
      url = "http://localhost:8080/board/update";
      send_param = {
        headers,
        "title": boardTitle,
        "content": boardContent
      };
    } else {
      url = "http://localhost:8080/board/write";
      send_param = {
        headers,
        "title": boardTitle,
        "content": boardContent
      };

    }



  onEditorChange = evt => {
    this.setState({
      data: evt.editor.getData()
    });
  };


    return (
      <div style={divStyle} className="App">
        <h2>글쓰기</h2>
        <Button
          type="text"
          style={titleStyle}
          placeholder="글 제목"
          ref={ref => (this.boardTitle = ref)}
        />
        <CKEditor
          data={this.state.data}
          onChange={this.onEditorChange}
        ></CKEditor>
        <Button style={buttonStyle} onClick={this.writeBoard} block>
          저장하기
        </Button>
      </div>
    );
  }
}

export default BoardWrite;
