import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom';
import { SidebarDate } from '../Sidebar/SidebarData';
import { IconContext } from 'react-icons';
import * as FaIcons from 'react-icons/fa';
// import * as GrIcons from 'react-icons/gr';
// import Sidebarmenu from '../Sidebar/Sidebarmenu';
import './Header.css';
import '../Sidebar/Sidebar.css';

function Header(props){
    const [sidebar,setSidebar] = useState(false);
    const showSidebar = () => setSidebar(!sidebar);

    let [userprofile, setUserprofile] = useState(false)
    let [userPhoto, setUserPhoto] = useState()
    let [currentUser_pk, setCurrentUser_pk] = useState();
    console.log(`${localStorage.getItem('token')}`);
    console.log(props.modal);
    useEffect(()=>{
        fetch('http://39.118.174.168:8000/api/current_user/', {
        headers: {
            Authorization: `JWT ${localStorage.getItem('token')}`
        }
    })
    .then(res => res.json())
    .then(json => {
      // 현재 유저 정보 받아왔다면, 로그인 상태로 state 업데이트 하고
        if (json.id) {
            console.log(json.id)
        //유저정보를 받아왔으면 해당 user의 프로필을 받아온다.
        }fetch('http://39.118.174.168:8000/api/profile/' + json.id + '/update/',{
        method : 'PATCH',
        headers: {
            Authorization: `JWT ${localStorage.getItem('token')}`
        },
        })
        .then((res)=>res.json())
        .then((userData)=> {
            setUserPhoto(userData.photo)
            setCurrentUser_pk(userData.user_id)
        })
        .catch(error => {
            console.log(error);
          });
    }).catch(error => {
        console.log(error)
    });
    },[userPhoto])

    return(
    <>
        <div className="header">
        <div className="header-nav">
        <div className="header-nav-links">
           
            {
              props.modal === false || props.modal === null || props.modal === undefined
              ? <Link to="/login"><button className="header-btn">로그인</button></Link>
              : (
                <>
                <Link className="header-dashboard" to="/write"><button>새 글 작성</button></Link>
                <div className="user-container" onClick={()=>{setUserprofile(!userprofile)}}>
                <div className='navbar'>
                  <img src={userPhoto} className="user-image"
                       onClick={showSidebar}>
                         
                       </img>
                </div>
                </div>
                {
                  userprofile === true
                  ?(
                    <>
                     {/* <div className="user-profile">
                       <div className="profile-menu">
                         <Link to={"/mysite/" + currentUser_pk}><div className="menu">내가 쓴 글</div></Link>
                         <Link to="/profile"><div className="menu">내 정보</div></Link>
                         <Link onClick={props.handleLogout} to="/"><div className="menu">로그아웃</div></Link>
                       </div>
                     </div> */}
                    
        {/* <IconContext.Provider value={{color: 'black' }}>
        <div className='navbar'>
            <Link to='#' className='menu-bars'>
                <FaIcons.FaBars onClick={showSidebar}/>
            </Link>
        </div>
        <nav className={sidebar ? 'nav-menu active' : 'nav-menu'}>
            <ul className='nav-menu-items' onClick={showSidebar}>
                <li className="navbar-toggle">
                    <Link to='#' className='menu-bars'>
                        
                    </Link>
                    
                </li>
                {SidebarDate.map((item, index) => {
                    return (
                        <li key={index} className={item.cName}>
                            <Link to={item.path}>
                                {item.icon}
                                <span>{item.title}</span>
                            </Link>
                        </li>
                    )
                })}
                
            </ul>
        </nav>
        </IconContext.Provider> */}
        </>
                  )
                  :null
                }
                </>
              )
            }
          </div>
        </div>
      </div>
    </>
  )
}

export default Header;
