import React, { useState } from "react";
import { withRouter } from "react-router-dom";
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';
// import { useDispatch } from "react-redux";
// import { loginUser } from "../_action/userAction";
import axios from "axios";
axios.defaults.withCredentials = true;

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="/">
        LoLtopia
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
 }

 const useStyles = makeStyles((theme) => ({
  paper: {
  
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '400px', // Fix IE 11 issue.
    marginTop: theme.spacing(2),
  },
  submit: {
    display: 'flex',
    alignitems: 'center',
    justifycontent: 'center',
    position: 'relative',
    margin: theme.spacing(2, 0, 2),
  },
 }));


function LoginPage(props) {
  // const dispatch = useDispatch();
  const [username, setUserName] = useState("");
  const [Password, setPassword] = useState("");
  
  const onUserNameHandler = (e) => {
    setUserName(e.currentTarget.value);
  };
  const onPasswordHanlder = (e) => {
    setPassword(e.currentTarget.value);
  };

  const onSubmitHandler = (e) => {
    e.preventDefault();
    //로그인을 진행하기위해서
    //첫번째 useDispatch(액션) 을 활용해서 액션을 dispatch해준다
    const data = {
      username: username,
      password: Password,
    };
    fetch('http://39.118.174.168:8000/api/login/', {
      method: 'POST',
      headers: {
        'Content-Type':'application/json'
      },
      body: JSON.stringify(data)
    })
    .then(res => res.json())
    .then(json => {
      if (json.user && json.user.username && json.token) {
        props.userHasAuthenticated(
          true, 
          json.user.username,
          json.token);
        props.history.push("/");
        props.setModal(true);
        console.log(json);
      } else {
        alert("아이디 또는 비밀번호를 확인해주세요.")
      }
    }).catch(error => alert(error));
  };

  

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        width: "100%",
        height: "100%",
      }}>
      <form
        onSubmit={onSubmitHandler} method="POST"
        style={{ display: "flex", flexDirection: "column" }}>
        <label>ID</label>
        <input type="username" value={username} onChange={onUserNameHandler} />
        <label>Password</label>
        <input type="password" value={Password} onChange={onPasswordHanlder} />
        <br />
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default withRouter(LoginPage);
