import axios from "axios";

const DOMAIN = "http://39.118.174.168:8000/";
const headers = {
  Authorization: `Token 1c6a00783f8cd0556ab3b7d7cbd47effb507fc41d6860fe34e4b2acbc3f861da`
};
axios.defaults.withCredentials = true; // 쿠키 데이터를 전송받기 위해
export const request = (method, url, data) => {
  return axios({
    method,
    url: DOMAIN + url,
    headers : headers,
    data,
  })
    .then((res) => res.data)
    .catch((err) => console.log(err));
};
