import { REGISTER_USER, LOGIN_USER, LOGOUT_USER } from "../_action/type"

const userReducer = (state=null, action)=>{
  switch(action.type){
    case REGISTER_USER:
      return {...state, success: action.payload};
    case LOGIN_USER:
      return { ...state, loginsuccess: action.payload };
    case LOGOUT_USER:
      return { ...state, success: action.payload };
    default:
      return state;
  }
};
export default userReducer;
