                    <div className="myProfile">
                        <div className="wrapper">
                            <div className="title-wrapper">
                                <h3>이메일</h3>
                            </div>
                            <div className="block-for-mobile">
                                    <div className="contents">{userEmail_info}</div>  
                                {
                                    pointModal === true
                                    ?(
                                        <form className="nickname-form">
                                            <input className="nickname-input" placeholder={""} onChange={(e)=>{
                                                setUserEmail_info(e.target.value)
                                            }}></input>
                                        </form>
                                        
                                    )
                                    :(
                                        <div className="edit-wrapper">
                                            
                                        </div>
                                    )
                                }
                            </div>
                        </div>    
                    </div>

                    <div className="myProfile">
                        <div className="wrapper">
                            <div className="title-wrapper">
                                <h3>이름</h3>
                            </div>
                            <div className="block-for-mobile">
                                    <div className="contents">{userName}</div>
                                {
                                    pointModal === true
                                    ?(
                                        <form className="nickname-form">
                                            <input className="nickname-input" placeholder={""} onChange={(e)=>{
                                                setUserName(e.target.value)
                                            }}></input>
                                        </form>
                                        
                                    )
                                    :(
                                        <div className="edit-wrapper">
                                            
                                        </div>
                                    )
                                }
                            </div>
                        </div>    
                    </div>
                    
                    <div className="myProfile">
                        <div className="wrapper">
                            <div className="title-wrapper">
                                <h3>전화번호</h3>
                            </div>
                            <div className="block-for-mobile">
                                <div className="contents">{userNumber}</div>
                                {
                                    pointModal === true
                                    ?(
                                        <form className="nickname-form">
                                            <input className="nickname-input" placeholder={""} onChange={(e)=>{
                                                setUserNumber(e.target.value)
                                            }}></input>
                                        </form>
                                        
                                    )
                                    :(
                                        <div className="edit-wrapper">
                                            
                                        </div>
                                    )
                                }
                            </div>
                        </div>    
                    </div>

                    
                    <div className="myProfile">
                        <div className="wrapper">
                            <div className="title-wrapper">
                            </div>
                            <div className="block-for-mobile">
                                <div className="contents">
                                    <button className="out-button" onClick={DeleteUser}>회원탈퇴</button>
                                    <button className="save-button" onClick={()=>{
                                        handleEffect(handleSubmit)
                                        setInfomodal(false)
                                        setNickname(false)
                                    }}>저장</button>
                                </div>
                            </div>
                        </div>
                    </div>
                    
                </section>
            </main>
        </>
    )
}
export default Profile;
