import React,{useState, useEffect} from 'react';
import {Link} from 'react-router-dom';
import '../views/Primium.css';

function Primium(){
    let [filterList] = useState([{id : 1, language: 'Python'}, {id : 2, language : 'React'},
    {id : 3, language : 'Java'}, {id : 4, language :'C#'}, {id : 5, language:'C'},
    {id : 6, language:'C++'}, {id : 7, language:'GO'}, {id : 8, language:'Javascript'}])
    let [languagefilterList, setLanguageilterList] = useState("");
    let [todolist, setTodoList] = useState([]);
    let [userNickname, setUserNickname] = useState("")
    let [userId, setUserId] = useState()
    let [userPhoto, setUserPhoto] = useState()
    let pgN = document.location.href.split('/')[3]
    
    // useEffect(()=>{
    //     pgN === "like"
    //     ?(
    //         fetch('http://39.118.174.168:8000/board/blog/like/')
    //         .then((res)=>res.json())
    //         .then((posts)=>{
    //             setTodoList(posts);
    //         })
    //     )
    //     :(
    //         fetch('http://39.118.174.168:8000/board/blog/')
    //         .then((res)=>res.json())
    //         .then((posts)=>{
    //             console.log(posts);
    //             setTodoList(posts)
    //         })
    //     )
    // },[pgN])

    const ClickFilter = (lang)=>{
        let Num = 0;
        let List = [...languagefilterList];

        List.map((a)=>{
            if (a === lang) {
                Num = 1;
            }
            return Num;
        })

        if (Num === 0) {
            List.push(lang)
        }else{
            List.splice(List.indexOf(lang),1)
        }
        setLanguageilterList(List)
    }

    useEffect(()=>{
        fetch('http://39.118.174.168:8000/api/current_user/', {
          headers: {
            Authorization: `JWT ${localStorage.getItem('token')}`
          }
        })
        .then(res => res.json())
        .then(json => {
          // 현재 유저 정보 받아왔다면, 로그인 상태로 state 업데이트 하고
          if (json.id) {
            setUserId(json.id)
            //유저정보를 받아왔으면 해당 user의 프로필을 받아온다.
        }fetch('http://39.118.174.168:8000/api/profile/' + json.id + '/update/',{
                method : 'PATCH',
                headers: {
                    Authorization: `JWT ${localStorage.getItem('token')}`
                },
            })
            .then((res)=>res.json())
            // 데이터베이스에서 가져오는 정확한 명
            .then((userData)=> {
                setUserNickname(userData.nickname);
                setUserPhoto(userData.photo);
                setUserId(userData.userId)
            })
            
            .catch(error => {
                console.log(error);
              });;
        }).catch(error => {
            console.log(error)
          });
          
    }
    ,[userId])


    return(
        <div className="trend-section">
            <main className="trend-main">
                <div className="main-section">
                        {
                            todolist.slice(0).reverse().map((a)=>{
                                let Y = a.pub_date.split('-')[0]
                                let M = a.pub_date.split('-')[1]
                                let D = a.pub_date.split('-')[2].split('T')[0]
                                let boardFilterArticle;
                                let Num;
                                let list =  a.information.split(',')

                                if(languagefilterList.length !== 0){
                                    languagefilterList.map((c)=>{
                                        if(list.indexOf(c) !== -1 && Num !== 0){
                                            Num = 1;
                                        }else{
                                            Num = 0;
                                            return Num;
                                        }
                                        return Num;
                                    })
                                    if(Num === 1){
                                        boardFilterArticle = 
                                        <div className="article" key={a.id}>
                                            {
                                                a.image === null
                                                ? null
                                                :(
                                                    <Link to={"/detail/" + a.id}>
                                                        <div className="arcticle-img">
                                                            {/* <img src={"http:/39.118.174.168:8000/" + a.image} alt=""></img> */}
                                                        </div>
                                                    </Link>
                                                )
                                            }
                                            <div className="article-content">
                                                <Link to={"/detail/" + a.id}>
                                                    <h4>{a.title}</h4>
                                                    <div className="desc-wrapper">
                                                        <p>{a.content}</p>
                                                    </div>
                                                </Link>
                                                <div className="sub-info">
                                                    <span>{Y + "년 " + M + "월 " + D + "일"}</span>
                                                </div>
                                                <div className="filter-info">
                                                {
                                                    list.map((L, i)=>{
                                                        return(
                                                            <p key={i}>{L}</p>
                                                        )
                                                    })
                                                }
                                            </div>
                                            </div>
                                            <div className="article-footer">
                                                <Link to={"/mysite/" + a.user_pk}>
                                                    <img src={userPhoto} alt=""></img>
                                                    <span>"by " <b>{userNickname}</b></span>
                                                </Link>
                                                <div className="likes">
                                                    <svg width="24" height="24" viewBox="0 0 24 24"><path fill="currentColor" d="M18 1l-6 4-6-4-6 5v7l12 10 12-10v-7z"></path></svg>
                                                    {a.like}
                                                </div>
                                            </div>
                                        </div>
                                    }
                                }else{
                                    boardFilterArticle = 
                                    <div className="article" key={a.id}>
                                        {
                                            a.image === null
                                            ? null
                                            :(
                                                <Link to={"/detail/" + a.id}>
                                                    <div className="arcticle-img">
                                                        {/* <img src={"http://localhost:8000" + a.image} alt=""></img> */}
                                                    </div>
                                                </Link>
                                            )
                                        }
                                        <div className="article-content">
                                            <Link to={"/detail/" + a.id}>
                                                <h4>{a.title}</h4>
                                                <div className="desc-wrapper">
                                                    <p>{a.content}</p>
                                                </div>
                                            </Link>
                                            <div className="sub-info">
                                                    <span>{Y + "년 " + M + "월 " + D + "일"}</span>                                                    
                                                </div>
                                            <div className="filter-info">
                                                {
                                                    list.map((L, i)=>{
                                                        return(
                                                            <p key={i}>{L}</p>
                                                        )
                                                    })
                                                }
                                            </div>
                                        </div>
                                        <div className="article-footer">
                                            {/* <Link to={"/mysite/" + a.id}> */}
                                                <img src={userPhoto} alt=""></img>
                                                <span>"by " <b>{userNickname}</b></span>
                                            {/* </Link> */}
                                            <div className="likes">
                                                <svg width="24" height="24" viewBox="0 0 24 24"><path fill="currentColor" d="M18 1l-6 4-6-4-6 5v7l12 10 12-10v-7z"></path></svg>
                                                {a.like}
                                            </div>
